  public SmallestDivisor(n: number) {
    for (let index = 1; index <= n; index++) {
      if (n % index == 0)
        return index;
    }
  }

  random(max: number, d?: number) {
    if (d != null) {
      var x = 0;
      do {
        x = Math.floor((Math.random() * max));
      }
      while (x == d);
      return x;
    }
    else
      return Math.floor((Math.random() * max));
  }
